<?phpclass userInfo extends extend{        var $db;    var $uid;    var $user_info_data;        public function init( $uid )        {        $this->loadComponent("database", "db");        $this->uid = $uid;        if( $this->db->user_info->where('uid', $uid)->fetch()->numRows() == 0 )            $this->db->user_info->insert('uid', $this->uid);        $this->user_info_data = $this->db->user_info->where('uid', $uid)->fetch()->row();        }            public function __set( $var, $value)        {        $this->db->user_info->where('uid', $this->uid)->update($var, $value);        $this->user_info_data->{$var} = $value;        }            public function __get( $var )        {        if( isset($this->user_info_data->$var) )            return $this->user_info_data->{$var};        }}